SELECT AJ.AJBH, A.S_CODE AS CODE, A.S_FULL_NAME AS FULL_NAME, T.S_CAPTION AS CAPTION, A.S_REASON AS REASON, p.S_NAME as NAME, A.S_DEPT AS DEPT, A.S_INQUEST_DEPT_CAPTION AS INQUEST_DEPT_CAPTION, A.DT_CREATE_TIME AS DT_CREATE_TIME, u.DT_START_USE_TIME as DT_START_USE_TIME, u.DT_END_USE_TIME as DT_END_USE_TIME, P.S_CREDENTIALS_NUM, p.S_DOMICILE, P.I_AGE AS AGE, CS.S_CAPTION AS SEX, D.S_CAPTION AS NATION, P.DT_BIRTHDAY AS BIRTHDAY, POLICE.POLICE_NAME FROM TAB_INQUEST_ARCHIVE A LEFT JOIN TAB_INQUEST_ARCHIVE_TYPE T ON A.I_TYPE_ID = T.I_ID LEFT JOIN TAB_INQUEST_STANDBOOK_USER U ON A.S_CODE = U.S_TABULATE_NUMBER LEFT JOIN TAB_PERSON P ON P.S_ID = U.S_PERSON_ID LEFT JOIN AJ_JBXX AJ ON AJ.BARZJHM = P.S_CREDENTIALS_NUM LEFT JOIN TAB_GA_CODE_SEX CS ON CS.I_ID = P.I_SEX LEFT JOIN TAB_GA_CODE_NATION D ON D.S_ID = P.S_NATION ,( SELECT LISTAGG(TO_CHAR(U.S_CAPTION), ',')WITHIN GROUP( ORDER BY U.S_CAPTION) AS POLICE_NAME FROM TAB_INQUEST_STANDBOOK_POLICE SP LEFT JOIN TAB_INQUEST_STANDBOOK_USER SU ON SU.S_ID = SP.S_STANDBOOK_ID LEFT JOIN TAB_USER U ON SP.S_USER_ID = U.S_ID WHERE SU.S_TABULATE_NUMBER = #{code} GROUP BY SP.S_STANDBOOK_ID ) POLICE WHERE A.S_FULL_NAME IS NOT NULL
AND A.S_CODE = #{code}
ORDER BY A.DT_CREATE_TIME DESC
